import { React, useState } from "react";

function ReservationFrom() {
  const [inputValue, setInputValue] = useState({
    username: "",
    email: "",
    phonenumber: "",
  });
  const changeHandler = (event) => {
    setInputValue({ [event.target.name]: event.target.name });
  };

  const { username, email, phonenumber } = inputValue;

  return (
    <div>
      <form action="">
        <div>
          <label>User Name</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={changeHandler}
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="text"
            name="email"
            value={email}
            onChange={changeHandler}
          />
        </div>
        <div>
          <label>Phone Number</label>
          <input
            type="text"
            name="phonenumber"
            value={phonenumber}
            onChange={changeHandler}
          />
        </div>
        <div>
          <button>Add Reservation</button>
        </div>
      </form>
    </div>
  );
}

export default ReservationFrom;
